import React from 'react';
import { SafeAreaView, StyleSheet, Text, View, Image } from 'react-native';

// import { Container } from './styles';

const HomePage = () => {
  return (
   <>
   <SafeAreaView style={styles.container}>
      <View style={styles.cardsContainer}>
         <View style = {styles.card}>
            <Image style={styles.img} source= {{uri: 'http://ddragon.leagueoflegends.com/cdn/10.18.1/img/profileicon/1159.png'}}/>
            <View style={styles.nameContainer}>
               <Text style={styles.name}>Nickname</Text>
               <Text style={styles.level}>Level: 155</Text>
            </View>
            <Image style={styles.elo} source= {{uri: 'https://vignette.wikia.nocookie.net/leagueoflegends/images/3/38/Season_2019_-_Unranked.png'}}/>
         </View>
      </View>
   </SafeAreaView>
   </>
  )
}

const styles = StyleSheet.create({
   container:{
      backgroundColor: "#f3f3f3",
      flex: 1,
      alignItems: "center",
      justifyContent: "center",      
   },
   img:{
      flex: 1,     
      width: 380,      
      alignItems: 'center',
   },
   elo:{
      width: 80,
      height: 80,      
      alignItems: 'center',
   },
   cardsContainer:{
      flex: 1,
      alignSelf: 'stretch',
      justifyContent: 'center',
      maxHeight: 600,
      
   },
   card: {
      borderWidth:1,
      borderColor: "#DDD",
      borderRadius: 8,
      margin: 30,
      overflow: 'hidden',
      alignItems: "center",
      position: 'absolute',
      top:0,
      left:0,
      right:0,
      bottom:0      
   },
   nameContainer: {
      backgroundColor: "#343A40",
      borderWidth:1,
      borderRadius: 5,
      marginTop: 10,
      paddingLeft: 20,
      paddingRight: 20,
      marginBottom: 15,
   },
   name: {
      fontSize: 22,
      color: '#fff',
      fontWeight: "500",    
   },
   level: {
      fontSize: 22,
      color: '#fff',
      fontWeight: "500",
   }
})

export default HomePage;
