import React from 'react';
import { KeyboardAvoidingView, Text, View, StyleSheet, Image, TextInput, TouchableOpacity, Platform } from 'react-native';



const HomeLogin = ({ navigation }) => {
  return (
     <KeyboardAvoidingView 
      style={styles.container} 
      behavior="padding"
      enabled={Platform.OS="ios"}>
        <Image style={styles.img} source = {{uri: 'https://i.imgur.com/Nk12wEK.png'}}/>
        <TextInput 
         autoCorrect={false}         
         autoCapitalize="none" 
         placeholderTextColor="#999" 
         placeholder="Nickname" 
         style={styles.input}>           
        </TextInput>
        <TouchableOpacity onPress={() => navigation.navigate('TinLegends')} style={styles.button}>
            <Text style={styles.buttonText}>Entrar</Text>
         </TouchableOpacity>
     </KeyboardAvoidingView>
  )
}

const styles  = StyleSheet.create({
   container:{
      backgroundColor: "#f3f3f3",
      flex: 1,
      alignItems: "center",
      justifyContent: "center",
      padding: 30,
   },
   img:{
      width: 350,
      height: 150,
   },
   input:{
      height: 56,
      alignSelf: "stretch",
      backgroundColor: "#ffffff",
      borderWidth: 1,
      borderColor: "#ddd",
      borderRadius: 4,
      marginTop: 10,
      paddingHorizontal: 15,

   },
   button:{
      backgroundColor: "#FFC107",
      height: 46,
      alignSelf: "stretch",
      borderRadius: 4,
      marginTop: 10,
      justifyContent: "center",
      alignItems: "center"
   },
   buttonText:{
      fontSize: 18,
      fontWeight: "400",
      

   },
})

export default HomeLogin;
