import React, {useEffect, useState} from 'react';
import { SafeAreaView, StyleSheet, Text, View, Image,  } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import like from '../images/like.png'
import dislike from '../images/dislike.png'
import api from '../services/api'

// import { Container } from './styles';

const HomePage = ({navigation}) => {
   const id = navigation.getParam('_id');
   console.log(id)
  return (
   <>
   <SafeAreaView style={styles.container}>
      <View style={styles.cardsContainer}>
         <View style = {styles.card}>
            <Image style={styles.img} source= {{uri: 'http://ddragon.leagueoflegends.com/cdn/10.18.1/img/profileicon/1159.png'}}/>
            <View style={styles.nameContainer}>
               <Text style={styles.name}>Nickname</Text>
               <Text style={styles.level}>Level: 155</Text>
            </View>
            <Image style={styles.elo} source= {{uri: 'https://vignette.wikia.nocookie.net/leagueoflegends/images/3/38/Season_2019_-_Unranked.png'}}/>
         </View>
      </View>
      <View style={styles.buttonsContainer}>
         <TouchableOpacity style={styles.button}>
            <Image source={like}></Image>            
         </TouchableOpacity>
         <TouchableOpacity style={styles.button}>
            <Image source={dislike}></Image>            
         </TouchableOpacity>
      </View>
   </SafeAreaView>
   </>
  )
}

const styles = StyleSheet.create({
   container:{
      backgroundColor: "#f3f3f3",
      flex: 1,
      alignItems: "center",
      justifyContent: "center",      
   },
   img:{
      flex: 1,     
      width: 380,      
      alignItems: 'center',
   },
   elo:{
      width: 80,
      height: 80,      
      alignItems: 'center',
      marginBottom: 15,
   },
   cardsContainer:{
      flex: 1,
      alignSelf: 'stretch',
      justifyContent: 'center',
      maxHeight: 600,
      
   },
   card: {
      borderWidth:1,
      borderColor: "#DDD",
      borderRadius: 8,
      margin: 30,
      overflow: 'hidden',
      alignItems: "center",
      position: 'absolute',
      top:0,
      left:0,
      right:0,
      bottom:0      
   },
   nameContainer: {
      backgroundColor: "#343A40",
      borderWidth:1,
      borderRadius: 5,
      marginTop: 10,
      paddingLeft: 20,
      paddingRight: 20,
      marginBottom: 15,
   },
   name: {
      fontSize: 22,
      color: '#fff',
      fontWeight: "500",    
   },
   level: {
      fontSize: 22,
      color: '#fff',
      fontWeight: "500",
   },
   buttonsContainer:{
      flexDirection: "row",
      marginBottom: 30,      
   },
   button:{
      width: 50,
      height: 50,
      borderRadius: 25,
      backgroundColor: "#FFF",
      justifyContent: 'center',
      alignItems: "center",
      marginHorizontal: 20,
      elevation: 2,
      shadowColor: '#000',
      shadowOpacity: 0.05,
      shadowRadius: 2,
      shadowOffset:{
         width:0,
         height: 2,
      }
   }
})

export default HomePage;
